import React, { useState, useEffect } from "react";
import Card from 'react-bootstrap/Card';

const Product =() => {
  const [data, setData] = useState([]);
  const fetchData = async () => {
      const response = await fetch(`http://localhost:8000/products`);
      const result = await response.json();
      setData(result);
    };
  useEffect(() => {
    fetchData();
  }, []);
  return (
    <>
      <div className="App container">
        <h1 style={{ color: "red" }}>Shop Collections</h1>
        <div className="main-outer row">
          {data.map((item) => (

        <div style={{ width: "18rem" }} className="main-inner col-sm-3">
         <p>{item.category}</p>
         <h4>{item.title}</h4>
         <span className="product-price">${item.price}</span>
         <span className="product-camparePrice"><s>${item.camparePrice}</s></span>
         <p className="product-description-collection">{item.description}â€¦</p>
       </div>
          ))}
        </div>
      </div>
      <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src="holder.js/100px180" />
      <Card.Body>
        <Card.Title>Card Title</Card.Title>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
        <button variant="primary">Go somewhere</button>
      </Card.Body>
    </Card>
    </>
  );
};
export default Product;
