import React, { useState, useEffect } from "react";
import Card from 'react-bootstrap/Card';
const Product =() => {
  const [data, setData] = useState([]);
  const fetchData = async () => {
      const response = await fetch(`http://localhost:8000/products`);
      const result = await response.json();
      setData(result);
    };
  useEffect(() => {
    fetchData();
  }, []);
   return (
  <>
    <div className="App container">
      <h1 style={{ color: "red" }}>Shop Collections</h1>
      <div className="main-outer row">
        {data.map((item) => (
    <Card style={{ width: "18rem" }} className="main-inner col-sm-3">

      <div className="main-product-container">
      <p>{item.category}</p>
      <img width="100%" height="200px" src={`http://localhost:8000/publi/files/${item?.image}`} />


      <div className="product-title">{item?.title}</div>
      </div>
      <div className="price-with-camparePrice">
      <span className="product-price">${item?.price}</span>
      <span className="product-camparePrice"><s>${item?.camparePrice}</s></span>
      </div>
      <div className="product-description">
      {item?.description}
      </div>
      <button variant="primary">Add To Cart</button>

    </Card>
        ))}
      </div>
    </div>
  </>
  );
};
export default Product;
