import React, { useState, useEffect } from "react";
const Product =() => {
  const [data, setData] = useState([]);
  const fetchData = async () => {
      const response = await fetch(`http://localhost:8000/products`);
      const result = await response.json();
      setData(result);
    };
  useEffect(() => {
    fetchData();
  }, []);
  return (
    <>
      <div className="App container">
        <h1 style={{ color: "red" }}>Shop Collections</h1>
        <div className="main-outer row">
          {data.map((item) => (
       <div style={{ width: "18rem" }} className="main-inner col-sm-3" key={item.id}>
         <h4>{item.title}</h4>
         <span className="product-price">${item.price}</span>
         <span className="product-camparePrice"><s>${item.camparePrice}</s></span>
         <p className="product-description-collection">{item.description.slice(0, 150)}â€¦</p>

       </div>
          ))}
        </div>
      </div>
    </>
  );
};
export default Product;
