import React, { useState, useEffect } from "react";
import Card from 'react-bootstrap/Card';
const Product =() => {
  const [data, setData] = useState([]);
  const fetchData = async () => {
      const response = await fetch(`http://localhost:8000/products`);
      const result = await response.json();
      setData(result);
    };
  useEffect(() => {
    fetchData();
  }, []);
   return (
    <>
      <div className="App container">
        <h1 style={{ color: "red" }}>Shop Collections</h1>
        <div className="main-outer row">
          {data.map((item) => (
      <Card style={{ width: "18rem" }} className="main-inner col-sm-3">
      <Card.Img variant="top" src="holder.js/100px180" />
      <Card.Body>
        <div className="">
        <p>{item.category}</p>
        <img className="product-item-image" src={"http://127.0.0.1:8000/public/files" + item.image} alt="image" />

        <div className="product-title">{item.title}</div>
        </div>
        <div className="price-with-camparePrice">
        <span className="product-price">${item.price}</span>
        <span className="product-camparePrice"><s>${item.camparePrice}</s></span>
        </div>
        <div className="product-description">
        {item.description}
        </div>
        <button variant="primary">Add To Cart</button>
      </Card.Body>
    </Card>
          ))}
        </div>
      </div>

    </>
  );
};
export default Product;
